import React from 'react';

class TodoList extends React.Component {
    state = {
        newTodo: '',
    };
    addTodo = (e) => {
        e.preventDefault();
        this.props.addTodo(this.state.newTodo);
    };
    handleChanges = (e) => this.setState({ newTodo: e.target.value });

    render() {
        return (
            <div>
                <h2>Todo: </h2>
            </div>
        );
    }
}

export default TodoList;
