import React from 'react';
import { connect } from 'react-redux';
import ListItem from './ListItem';

import { addTodo } from '../actions';

const List = (props) => {
    addTodo = (e) => {
        e.preventDefault();
        this.props.addTodo(this.state.newTodo);
    };
    const handleChanges = (e) => this.setState({ newTodo: e.target.value });

    return (
        <div>
            <h2>Todo: </h2>
            {props.todoList.map((value) => (
                <ListItem item={value.item} key={value.id} />
            ))}
            <div>
                <input onChange={handleChanges} value={this.state.newTodo} />
                <button onClick={this.addTodo}>Add Todo</button>
            </div>
        </div>
    );
};
const mapStateToProps = (state) => {
    return {
        todoList: state.todoList,
    };
};
export default connect(mapStateToProps, { addTodo })(List);
